import React, { useEffect, useState } from "react";
import "./MovieList.css";
import "./Row.css";

function MovieList() {
  const [movies, setMovies] = useState([
    {
      Title: "Alien",
      Year: "1979",
      imdbID: "tt0078748",
      Type: "movie",
      Poster:
        "https://m.media-amazon.com/images/M/MV5BMmQ2MmU3NzktZjAxOC00ZDZhLTk4YzEtMDMyMzcxY2IwMDAyXkEyXkFqcGdeQXVyNzkwMjQ5NzM@._V1_SX300.jpg",
    },
    {
      Title: "Alien³",
      Year: "1992",
      imdbID: "tt0103644",
      Type: "movie",
      Poster:
        "https://m.media-amazon.com/images/M/MV5BYTNiYmQzNTctNzAyZC00ODY2LWE3ZjgtODU1NDA0NGI5ZDY1XkEyXkFqcGdeQXVyMTMxODk2OTU@._V1_SX300.jpg",
    },
    {
      Title: "Alien: Covenant",
      Year: "2017",
      imdbID: "tt2316204",
      Type: "movie",
      Poster:
        "https://m.media-amazon.com/images/M/MV5BYzVkMjRhNzctOGQxMC00OGE2LWJhN2EtNmYyODRiMDNlM2ZmXkEyXkFqcGdeQXVyMTMxODk2OTU@._V1_SX300.jpg",
    },
    {
      Title: "Alien: Resurrection",
      Year: "1997",
      imdbID: "tt0118583",
      Type: "movie",
      Poster:
        "https://m.media-amazon.com/images/M/MV5BNDljNGZkNmItNDlmMi00YzJhLWJiYWUtNGY4OGEwNmY0ODg4XkEyXkFqcGdeQXVyNTAyODkwOQ@@._V1_SX300.jpg",
    },
  ]);

  const [searchValue, setSearch] = useState("");

  useEffect(() => {
    async function fetchData() {
      const url = "http://www.omdbapi.com/?s=gladiator&apikey=fe790038";
      const response = await fetch(url);
      const responseJson = await response.json();
      setMovies(responseJson.Search);
      return responseJson;
    }
    fetchData();
  }, []);

  return (
    <div className="row">
      <div className="row_posters">
        {movies.map((movie, index) => (
          <img
            className="row_posterLarge"
            src={movie.Poster}
            alt={movie.Title}
          />
        ))}
      </div>
    </div>
  );
}

export default MovieList;
